import React, {useEffect, useState} from "react";
import Card from "../UI/Card";
import "./ProductType.css";
import createApiClientHttp from "../../client/clientHttp.js";
import {errorCode} from "../../ErrorCodeGui"

const ProductType = (props) => {
    console.log("ProductType")
    const apiClientHttp = createApiClientHttp();
    const [enteredError, SetError] = useState("");
    let productID = props.productID;
    const [rateMe, setRate] = useState(5);
    const [enteredProductInfo, SetProductInfo] = useState([{name:"",description:"",category:"",rate:""}]);
    const [namee, setnamee] = useState();

    let name=""
    const rateHandler = event => {
        setRate(event.target.value);
    };

    //todo: rate product
    async function rateClickHandler() {

        // const getAllStoresResponse = await apiClientHttp.setRate(productID,rateMe);

        setRate(rateMe);
    };
    //getInfoProduct
    //todo:get the info of the product


    console.log("StoreList")

    async function getProductInfo() {
        let INFO = [];

        const getProductTypeInfoResponse = await apiClientHttp.getProductTypeInfo(productID);
        console.log("start func  getAllStores")
        let str = JSON.stringify(getProductTypeInfoResponse);
        console.log("sendRulesResponse    " + str)
        console.log("getProductTypeInfoResponse.value.productName    " + getProductTypeInfoResponse.value.productName)
        console.log("getProductTypeInfoResponse.value.productName    " + getProductTypeInfoResponse.value.description)
        console.log("getProductTypeInfoResponse.value.productName    " + getProductTypeInfoResponse.value.category)
        console.log("getProductTypeInfoResponse.value.productName    " + getProductTypeInfoResponse.value.rate)

        if (getProductTypeInfoResponse.errorMsg !== -1) {
            SetError(errorCode.get(getProductTypeInfoResponse.errorMsg))
        } else {
            INFO.push({
                name: getProductTypeInfoResponse.value.productName,
                description: getProductTypeInfoResponse.value.description,
                category: getProductTypeInfoResponse.value.category,
                rate: getProductTypeInfoResponse.value.rate
            })
            setnamee(getProductTypeInfoResponse.value.productName)
            SetError("")
            SetProductInfo(INFO)
        }
    }
    //
    useEffect(() => {
        getProductInfo();
    }, [enteredProductInfo.refresh]);
     console.log('enteredProductInfo',enteredProductInfo[0].rate)
    // console.log(enteredProductInfo.length)

    // let enteredProductInfo = {
    //   name: "Hary Potter",
    //   description: "amazing",
    //   category: "Books",
    //   rate: rateMe,
    // };
    return (
        <Card>
            <div className="productType">
                <div>
                    <h3>Product Number: {productID}</h3>
                </div>
                <div>
                    <h3>Name: {namee}</h3>
                </div>
                {/*<div>*/}
                {/*    <h3>Description: {enteredProductInfo[0]}</h3>*/}
                {/*</div>*/}
                {/*<div>*/}
                {/*    <h3>Category: {enteredProductInfo[0]}</h3>*/}
                {/*</div>*/}
                {/*<div>*/}
                {/*    <h3>Rate: {enteredProductInfo[0]}</h3>*/}
                {/*</div>*/}
                <div className="bar__control">
                    <label>Rate Product</label>
                    <input
                        type="number"
                        min="0"
                        max="10"
                        value={rateMe}
                        onChange={rateHandler}
                        placeholder="1-10"
                    />
                    <button onClick={rateClickHandler}>Rate</button>
                </div>
            </div>
        </Card>
    );
};

export default ProductType;
