import React, {useEffect, useState} from "react";
import BIDItem from "./BIDItem";
import createApiClientHttp from "../../../client/clientHttp.js";
import {errorCode} from "../../../ErrorCodeGui"
const MainBID = (props) => {
  console.log("MainBID")
  const apiClientHttp = createApiClientHttp();
  const [enteredError, SetError] = useState("");
  const [allProducts, setallProducts] = useState([]);

  let UUID = props.uuid;
  //todo: get all my products
  async function getAllProducts() {
    let products = []
    const getAllProductsResponse = await apiClientHttp.getAllProducts();
    console.log("start func  getAllProducts")
    console.log(getAllProductsResponse)

    if (getAllProductsResponse.errorMsg !== -1) {
      SetError(errorCode.get(getAllProductsResponse.errorMsg))
    } else {
      console.log("elseelseelseelseelseelseelseelseucts")

      // products.
      for (let i = 0; i < getAllProductsResponse.value.length; i++) {
        products.push({id: getAllProductsResponse.value[i].productID, name: getAllProductsResponse.value[i].productName})
      }
      SetError("")
      console.log(products)
      setallProducts(products)
    }
  }

  useEffect(() => {
    getAllProducts();
  }, [allProducts.refresh]);

  let products = [
    { id: 1, name: "Sony 5", price: Math.random() * 100 },
    { id: 2, name: "TV", price: Math.random() * 100 },
    { id: 3, name: "Car KIA RIO", price: Math.random() * 100 },
    { id: 4, name: "AC/DC", price: Math.random() * 100, amount: 7 },
    { id: 5, name: "MAMA Yokero", price: Math.random() * 100 },
    { id: 6, name: "Iphone 13", price: Math.random() * 100 },
  ];
  let sum = 0;
  for (let i = 0; i < allProducts.length; i++) {
    sum += products[i].price * products[i].amount;
  }

  const [total, setTatal] = useState(sum);

  const cancelHandler = (price) => {
    setTatal(total - price);
  };

  let productIDs = products.map((expense) => (
    <BIDItem
      id={expense.id}
      name={expense.name}
      price={expense.price}
      onCancel={cancelHandler}
      uuid={UUID}
    />
  ));

  return (
    <div>
      <ul className="products-list">{productIDs}</ul>
    </div>
  );
};

export default MainBID;
