import React, { useState } from "react";
import "./StoreID.css";
import Card from "../UI/Card";

const StoreID = (props) => {
  console.log("StoreID" +props.open)
  const [enteredOpenCloseStore, ] = useState("");
  if (props.open){
    SetpenCloseStore('open')
  }
  else
  {
    SetpenCloseStore('close')
  }
  const clickHandler = () => {
    props.onEnterToStore(props.id);
  };
  return (
    <li>
      <Card className="store-item">
        <div className="store-item__price">#{props.id}</div>
        <div className="store-item__description">
          <h2>{props.name}</h2>
          <h2>store is {enteredOpenCloseStore}</h2>
          <button onClick={clickHandler}>inter to the store</button>
        </div>
      </Card>
    </li>
  );
};

export default StoreID;
