    const readMoreHandler = productID => {
        props.onReadMore(productID);
    };

    if (props.search != ""){
        let prod = allProducts.filter((product) => product.id === parseInt(props.search));
        if (prod.length!=0){
        let expensesContent =
            <ProductID id={prod[0].id} name={prod[1].name} onReadMore={readMoreHandler}/>
        
