      };
      return (
        <li>
          <Card className="product-item">
            <div className="product-item__price">#{productID}</div>
            <div className="product-item__description">
              <h2>{name}</h2>
              <h2>price: {price}</h2>
              <h2>amount: {amount}</h2>
              <button onClick={clickHandler}>cencel</button>
            </div>
          </Card>
        </li>
      );
};

export default ProductInCart;
