import React, {useEffect, useState} from "react";
import BuyCart from "./BuyCart";

import createApiClientHttp from "../../client/clientHttp.js";
import {errorCode} from "../../ErrorCodeGui"

import ProductInCart from "../Product/ProductInCart";

const MyCart = (props) => {
  console.log("MyCart")
  const [Cartproducts, SetProducts] = useState([]);
  const apiClientHttp = createApiClientHttp();
  const [enteredError, SetError] = useState("");
  const [total, setTatal] = useState(0);

  let UUID = props.uuid;
  //todo: get all my products
  //getShoppingCart
  async function getMyCart() {
    let products = [];
    console.log(" getShoppingCart UUID",UUID)

    const getShoppingCartResponse = await apiClientHttp.getShoppingCart(UUID);
    console.log("start func getMyCart getShoppingCart")
    let str = JSON.stringify(getShoppingCartResponse);
    console.log("sendRulesResponse    "+str)

//todo צריך לוודא שבאמת אלה השדות ומה חוזר לי מבריפסונס הזה
    if (getShoppingCartResponse.errorMsg !== -1) {
      SetError(errorCode.get(getShoppingCartResponse.errorMsg))
    } else {
      let shoppingBagHash =getShoppingCartResponse.value.shoppingBagHash
      for (const key in shoppingBagHash) { //key = storeID
        let store = JSON.stringify(shoppingBagHash[key].store);//{"storeId":1,"name":"sys","founder":"sysManager@gmail.com","isOpen":true,"rate":5}
        let productQuantity = (shoppingBagHash[key].productQuantity); //{1:20, 2:50} product id and quantity
        for (const key in productQuantity) {
          console.log(key)
          console.log(productQuantity[key])
          products.push({
            id: key,
            // price: store.price,
            price:-50,
            amount: productQuantity[key],
            // name: getShoppingCartResponse.value[i].amount
          })

        }
        // console.log("trw[key]    "+str)
        // console.log(trw[key].storeI);

      }
      let sum = 0;
      for (let i = 0; i < products.length; i++) {
        console.log("DSAd")
        sum += products[i].price * products[i].amount;
      }
      setTatal(sum)
      SetError("")
      SetProducts(products);
    }
  }

  useEffect(() => {
    getMyCart();
  }, [Cartproducts.refresh]);

  console.log(Cartproducts)
  console.log(Cartproducts.length)

  // let products = [
  //   { id: 1, price: Math.random() * 100, amount: 3, name: "a product" },
  //   { id: 2, price: Math.random() * 100, amount: 4, name: "b product" },
  //   { id: 3, price: Math.random() * 100, amount: 3, name: "c product" },
  //   { id: 4, price: Math.random() * 100, amount: 7, name: "d product" },
  //   { id: 5, price: Math.random() * 100, amount: 1, name: "e product" },
  //   { id: 6, price: Math.random() * 100, amount: 12, name: "f product" },
  // ];
  // let sum = 0;
  // for (let i = 0; i < Cartproducts.length; i++) {
  //   console.log("DSAd")
  //   sum += Cartproducts[i].price * Cartproducts[i].amount;
  // }


  console.log("sum 1",total)

  const cancelHandler = (price) => {
    setTatal(total - price);
  };

  let productIDs = Cartproducts.map((expense) => (
    <ProductInCart
      id={expense.id}
      // name={expense.name}
      amount={expense.amount}
      price={expense.price}
      onCancel={cancelHandler}
      uuid={UUID}
    />
  ));
  const [command, setCommand] = useState(
    <div>
      {/*<ul className="products-list">{productIDs}</ul>*/}

      {/*<h3>Total Price: ${total}</h3>*/}
    </div>
  );

  const buyHandler = () => {
    setCommand(<BuyCart uuid={UUID} onMarket={()=>props.onMarket()}/>);
  };

  return (
    <div>
      <div style={{ color: 'black',position: 'relative',background: '#c51244',fontSize: 15 }}>{enteredError}</div>
      <h3>My Cart</h3>
      <h3>Total Price: ${total}</h3>

      <button onClick={buyHandler}> Buy All The Cart</button>
      {command}
      <ul className="products-list">{productIDs}</ul>

    </div>
  );
};

export default MyCart;
