import React, { useState } from "react";
import Card from "../UI/Card";
import "./Bar.css";
import SignUp from "./SignUp";
import SignIn from "./Login";
import {createApiClientHttp} from "../../client/clientHttp";
import {errorCode} from "../../ErrorCodeGui"
import SockJS from 'sockjs-client';
import Stomp from 'webstomp-client';
const Bar = (props) => {
  console.log("Bar")
  let stompClient = null; //save this instance
  let serverChannelURI = "http://localhost:8090/notifications";
  //connect()
  const [BARnotification, setBARNotification] = useState("first noty");
  let UUID='1'
  const [Appnotification, setAppNotification] = useState("first noty");

  const connect = () => {
    var socket = new SockJS(serverChannelURI);
    console.log("connect 1");

    stompClient = Stomp.over(socket);
    console.log("connect 2");


    stompClient.debug = () => {
    }; //to remove the debug of sockjs with stomp.
    console.log("connect 3");

    stompClient.connect({}, onConnected, onError);
    console.log("connect connect connect");

  }

  let onConnected = () => {

    // Subscribe to the notifications from server
    stompClient.subscribe("/user/notification/item", onMessageReceived);
    console.log("********************* onConnected UUID " + UUID);

    // Tell your 'uuid' to the server - start the session through webSocket
    stompClient.send("/ws/start/" + UUID)
  };

//Call this from logout() or guestLeave()
  const onDisconnect = () => {
    console.log("onDisconnect 1");
    console.log("********************* onDisconnect UUID " + UUID);

    // Tell your 'uuid' to the server - end the session through webSocket
    stompClient.send("/ws/stop/" + UUID)
    console.log("onDisconnect 1end");

  };

  let onMessageReceived = (payload) => {
    var message = payload.body;
    console.log("Server message: [" + message + "]"); //for testing
    setBARNotification(message)
    // setAppNotification(() => message)
    // setNotification(message);
    //do what ever we want to do with the message (string)
  }

  // props.onMessageReceived()

  let onError = () => {
    console.log("Error on attemp to connect with web-socket [is server on?]");
    //show an error on to the user
  }

  let apiClientHttp = createApiClientHttp();
  const [enteredError, SetError] = useState("");

  const [isLogin, setIsLogin] = useState(false);

  const [userName, setUserName] = useState("");


  const [email, setEmail] = useState("");
  const emailChangeHandler = (event) => {
    setEmail(event.target.value);
  };

  const [password, setPassword] = useState("");
  const passChangeHandler = (event) => {
    setPassword(event.target.value);
  };


  //todo: login after that the UUID put on onLogin(<UUID>)
  async function loginHandler(event) {
    event.preventDefault();
    // const Response = await apiClientHttp.getAllStores();

    const guestVisitResponse = await apiClientHttp.guestVisit();
    // const loginResponse = await apiClientHttp.login(guestVisitResponse.value, email, password);
    const loginResponse = await apiClientHttp.login(guestVisitResponse.value, "sysManager@gmail.com", "Shalom123$");


    if (loginResponse.errorMsg!== -1) {
      SetError(errorCode.get(loginResponse.errorMsg))
    } else {
      UUID=loginResponse.value
      connect();
      console.log(loginResponse)
      setUserName(email);
      setIsLogin(true);
      props.onLogin(loginResponse.value,email);
    }

  }

  async function logoutHandler(event) {
    event.preventDefault();
    const logoutResponse = await apiClientHttp.logout(props.uuid);

    if (logoutResponse.errorMsg!== -1) {
      SetError(errorCode.get(logoutResponse.errorMsg))
    } else {
      UUID=props.uuid
      onDisconnect()
      setEmail("");
      setPassword("");
      props.onLogout(logoutResponse.value);
      setIsLogin(false);
    }
    console.log(logoutResponse)

  }


  const initHandler = () => {
    props.onInitMarket();
  };

  const singUpHandler = () => {
    props.onSignUp();
  };

  const newUserHolder = (userData) => {
    console.log(userData);
  };

  let command = "";
  if (isLogin === false) {
    command = (

    <div className="bar__controls">
      <h2>BARnotification {BARnotification}</h2>

      <div style={{ color: 'red',backgroundColor: "black",fontSize: 30 }}>{enteredError}</div>
        <div className="bar__control">
          <label>User's Email</label>
          <input type="text" value={email} onChange={emailChangeHandler} />
        </div>
        <div className="bar__control">
          <label>Password</label>
          <input type="text" value={password} onChange={passChangeHandler} />
        </div>
        <div className="bar__actions">
          <button className="bar__button" onClick={loginHandler} type="submit">
            Login
          </button>
          <button
            className="signUp__button"
            onClick={singUpHandler}
            // onUserData={newUserHolder}
          >
            Sign-Up
          </button>
          <button className="bar__button" onClick={initHandler}>
            {" "}
            Init-Market
          </button>

        </div>
      </div>
    );
  } else {
    command = (
      <div>
        <h2>BARnotification {BARnotification}</h2>

        <label>welcome {userName}</label>
        <div className="bar__actions">
          <button
            className="signUp__button"
            onClick={logoutHandler}
            // onUserData={newUserHolder}
          >
            LogOut
          </button>
        </div>
      </div>
    );
  }

  return <Card className="bar">{command}</Card>;
};

export default Bar;
