    json =await apiClientHttp.filterByName([2,3],"Dsd");
    console.log(json)
    json =await apiClientHttp.searchProductByDesc("Dds");
    console.log(json)
    json =await apiClientHttp.searchProductByDescList([1,2],"Fdf");
    console.log(json)
    json =await apiClientHttp.filterByDesc([1,2],"Dsds");
    console.log(json)
