    console.log(json)
    json =await apiClientHttp.filterByRate([2,3],33);
    console.log(json)
    json =await apiClientHttp.searchProductByCategory(3);
    console.log(json)
    json =await apiClientHttp.searchProductByCategoryList([1],3);
    console.log(json)
    json =await apiClientHttp.filterByCategory([2],3);
    console.log(json)
    json =await apiClientHttp.searchProductByStoreRate(3);
    console.log(json)
    json =await apiClientHttp.searchProductByStoreRateList([3],3);
    console.log(json)
    json =await apiClientHttp.filterByStoreRate([3],2);
    console.log(json)
    let k =await apiClientHttp.searchProductByRangePrices(3,3,2);
    console.log(k,"Ddd")
    k =await apiClientHttp.searchProductByRangePricesList([2],2,2,2);
    console.log(k)
    k =await apiClientHttp.filterByRangePrices([2],2,2);
    console.log(k)
    console.log("getallusers")

    //getallusers
    k=await apiClientHttp.getAllMembers(guestVisitResponse.value)
    console.log(k)
    console.log("getStoreRoles")
    k=await apiClientHttp.getStoreRoles(guestVisitResponse.value,"2")
    console.log(k)



    console.log("getDiscountPolicy")
    k=await apiClientHttp.getDiscountPolicy(guestVisitResponse.value,"2")
    console.log(k)

    console.log("getDiscountPolicy")
    k=await apiClientHttp.getBuyPolicy(guestVisitResponse.value,"2")
    console.log(k)


    console.log("removeMember")
    k=await apiClientHttp.removeMember(guestVisitResponse.value,"d@gmail.com")
    console.log(k)

    console.log("removeStoreOwner")
    k=await apiClientHttp.removeStoreOwner(guestVisitResponse.value,"2","d@gmail.com")
    console.log(k)


    console.log("getUserInfo")
    k=await apiClientHttp.getUserInfo(guestVisitResponse.value,"sysManager@gmail.com")
    console.log(k)

    console.log("addNewProductToStore")
    k=await apiClientHttp.addNewProductToStore(guestVisitResponse.value,"1","1","1","1")
    console.log(k)

    console.log("setProductPriceInStore")
    k=await apiClientHttp.setProductPriceInStore(guestVisitResponse.value,"1","1","1")
    console.log(k)

    console.log("setProductQuantityInStore")
    k=await apiClientHttp.setProductQuantityInStore(guestVisitResponse.value,"1","1","1")
    console.log(k)


    // const guestVisitResponse = await apiClientHttp.guestVisit();
