import React, {useState, useEffect} from "react";
import Card from "../UI/Card";
import "./Market.css";
import StoreList from "../Store/StoreList";
import MarketButton from "./MarketButton";
import ProductButton from "../Product/ProductButton";
import Store from "../Store/Store";
import MyCart from "../Cart/MyCart";
import ManagerButton from "../Manager/ManagerButton";
import NewStore from "../Store/newStore";
import Profile from "./Profile";
import Help from "./Help";
import SockJS from 'sockjs-client';
import Stomp from 'webstomp-client';

const Market = (props) => {
    console.log("Market")
    let UUID = props.uuid
    let OldUUID=props.olduuid
    // let isLogin=null
    // let try=props.try

    console.log("Market UUID: " + UUID)
    const [notification, setNotification] = useState("Notification");
    let stompClient = null; //save this instance
    let serverChannelURI = "http://localhost:8090/notifications";
    //connect()

    const connect = () => {
        var socket = new SockJS(serverChannelURI);
        console.log("connect 1");

        stompClient = Stomp.over(socket);
        console.log("connect 2");


        stompClient.debug = () => {
        }; //to remove the debug of sockjs with stomp.
        console.log("connect 3");

        stompClient.connect({}, onConnected, onError);
        console.log("connect connect connect");

    }

    let onConnected = () => {

        // Subscribe to the notifications from server
        stompClient.subscribe("/user/notification/item", onMessageReceived);
        console.log("********************* onConnected UUID " + UUID);

        // Tell your 'uuid' to the server - start the session through webSocket
        stompClient.send("/ws/start/" + UUID)
    };

//Call this from logout() or guestLeave()
    const onDisconnect = () => {
        console.log("onDisconnect 1");
        console.log("********************* onDisconnect UUID " + UUID);

        // Tell your 'uuid' to the server - end the session through webSocket
        // stompClient.send("/ws/stop/" + UUID)
        console.log("onDisconnect 1end");

    };

    let onMessageReceived = (payload) => {
        var message = payload.body;
        console.log("Server message: [" + message + "]"); //for testing
        // setNotification(message);
        //do what ever we want to do with the message (string)
    }

    let onError = () => {
        console.log("Error on attemp to connect with web-socket [is server on?]");
        //show an error on to the user
    }
    console.log("props.log " + props.log);
    console.log("props.uuid " + props.uuid);
    console.log("props.olduuid " + props.olduuid);

    if (props.uuid !== -1) {
        if (props.log === 'login') {
            console.log("in login " );

            // UUID='80452e94-78c6-40b6-972d-64fbf2b09f51'
            connect();
            // onDisconnect();
            console.log("after for  " );

            onDisconnect();
            console.log("after onDisconnect  " );
            console.log("after onDisconnect 2 " );


        }
        else if (props.log === 'logout'){
            console.log("in logout " );

            // onDisconnect();
            console.log("and diss111... ")
            // UUID=props.olduuid;
            // UUID='80452e94-78c6-40b6-972d-64fbf2b09f51'
            // onDisconnect();
            // stompClient.send("/ws/stop/" + UUID)


        }
    }

    const showStoreHandler = (storeID) => {
        console.log("showStoreHandler  storeID:" + storeID);
        setCommand(
            <Store storeID={storeID} uuid={UUID} onMarket={onMarketHandler}/>
        );
    };

    const [command, setCommand] = useState(
        <MarketButton onShowStore={showStoreHandler}/>
    );

    let permissonCommand = "";

    const managerHandler = () => {
        setCommand(<ManagerButton uuid={UUID}/>);
    };

    const openStoreHandler = () => {
        console.log("openStoreHandler UUID: " + UUID)
        setCommand(
            <NewStore
                uuid={UUID}
                useremail={props.useremail}
                onMarket={() => {
                    setCommand(
                        <MarketButton uuid={UUID} onShowStore={showStoreHandler}/>
                    );
                }}
            />
        );
    };

    const profileHandler = () => {
        setCommand(<Profile uuid={UUID} useremail={props.useremail}/>);
    };

    //todo: check permission if login
    if (props.uuid != -1) {
        permissonCommand = (
            <>
                <button onClick={openStoreHandler}> Open New Store</button>
                <button onClick={profileHandler}>Profile</button>
            </>
        );
    }

    //todo: check if manager
    if (props.uuid !== 7) {
        permissonCommand = (
            <>
                {permissonCommand}
                <button onClick={managerHandler}> Manager</button>
            </>
        );
    }

    const onMarketHandler = () => {
        setCommand(<MarketButton uuid={UUID} onShowStore={showStoreHandler}/>);
    };

    const MarketHandler = () => {
        setCommand(<MarketButton uuid={UUID} onShowStore={showStoreHandler}/>);
    };

    const productHandler = () => {
        setCommand(<ProductButton/>);
    };

    const helpHandler = () => {
        setCommand(<Help uuid={UUID} onHelp={(text) => setNotification(text)}/>);
    };

    const myCartHandler = () => {
        setCommand(
            <MyCart
                uuid={UUID}
                onMarket={() =>
                    setCommand(
                        <MarketButton uuid={UUID} onShowStore={showStoreHandler}/>
                    )
                }
            />
        );
    };

    return (
        <Card className="market">
            <h2>Notification:</h2>
            {notification}
            <div>

                <button onClick={MarketHandler}> Market</button>
                <button onClick={myCartHandler}> My Cart</button>
                <button onClick={productHandler}> Products</button>
                {permissonCommand}
                <button onClick={helpHandler}> Help!</button>
            </div>
            <div>{command}</div>
        </Card>
    );
};

export default Market;
